import profileImage from "../../images/2.jpg";
import { useContext } from "react";
import AboutMeContext from "../../context/AboutMeContext";

const AboutMeBio = () => {
  const { aboutMe } = useContext(AboutMeContext);

  return (
    <div className="block sm:flex sm:gap-10 mt-10 sm:mt-20">
      <div className="w-full sm:w-1/4 mb-7 sm:mb-0">
        <div
          className="rounded-lg overflow-hidden"
          style={{
            width: "100%", // Set the desired width of the container
            paddingBottom: "100%", // This creates a square container (1:1 aspect ratio)
          }}
        >
          <img
            src={profileImage}
            alt=""
            style={{
              display: "block",
              width: "100%", // Fill the entire container
              height: "auto", // Maintain the aspect ratio of the image
              objectFit: "cover", // Crop and center the image
            }}
          />
        </div>
      </div>

      <div className="font-general-regular w-full sm:w-3/4 text-left">
        {aboutMe.map((bio) => (
          <p
            className="mb-4 text-ternary-dark dark:text-ternary-light text-lg"
            key={bio.id}
          >
            {bio.bio}
          </p>
        ))}
      </div>
    </div>
  );
};

export default AboutMeBio;
